import React from "react";
import {
  Divider,
  Form,
  Select,
  Input,
  InputNumber,
  DatePicker,
  TimePicker,
  Button,
  Radio
} from "antd";
import { Grid, Row, Col } from "react-flexbox-grid";
import "./addtrade.css";

const { Option } = Select;

function onChange(value) {
  console.log(`selected ${value}`);
}

function onBlur() {
  console.log("blur");
}

function onFocus() {
  console.log("focus");
}

function onSearch(val) {
  console.log("search:", val);
}

const Addtrade = () => (
  <>
    <section style={{ textAlign: "center" }}>
      <h1 style={{ textAlign: "center" }}>Add new trade</h1>
    </section>
    <Grid fluid>
      <Row left="xs">
        <Col xs />
        <Col xs={32}>
          <Form layout="inline" style={{ marginTop: 32 }} labelAlign="right">
            <Row between="xs">
              <Col>
                <Form.Item
                  label="Buying party:"
                  labelCol={{ span: 8 }}
                  wrapperCol={{ span: 16 }}
                >
                  <Input placeholder="Yourself" />
                </Form.Item>
              </Col>
              <Col>
                <Form.Item
                  label="Selling party:"
                  labelCol={{ span: 8 }}
                  wrapperCol={{ span: 16 }}
                >
                  <Input placeholder="Them" />
                </Form.Item>
              </Col>
            </Row>
            <Row between="xs">
              <Col />
              <Col>
                <Form.Item
                  label="Product / Stock:"
                  labelCol={{ span: 10 }}
                  wrapperCol={{ span: 14 }}
                >
                  <Radio.Group buttonStyle="solid">
                    <Radio.Button value="a">Product</Radio.Button>
                    <Radio.Button value="b">Stock</Radio.Button>
                  </Radio.Group>
                </Form.Item>
              </Col>
              <Col />
            </Row>
            <Row between="xs">
              <Form.Item
                label="Product name: "
                labelAlign="left"
                labelCol={{ span: 10 }}
                wrapperCol={{ span: 14 }}
              >
                {
                  // Function to disable this option if the user selected "stock above"
                }
                <Select
                  showSearch
                  // style={{ width: 140 }}
                  placeholder="Product name"
                  // optionFilterProp="children"
                  onChange={onChange}
                  onFocus={onFocus}
                  onBlur={onBlur}
                  onSearch={onSearch}
                  filterOption={(input, option) =>
                    option.props.children
                      .toLowerCase()
                      .indexOf(input.toLowerCase()) >= 0
                  }
                >
                  <Option value="jack">Jack</Option>
                  <Option value="lucy">Lucy</Option>
                  <Option value="tom">Tom</Option>
                </Select>
              </Form.Item>
              <Form.Item
                label="Quantity: "
                labelAlign="left"
                labelCol={{ span: 8 }}
                wrapperCol={{ span: 16 }}
              >
                <InputNumber style={{ width: 160 }} />
              </Form.Item>
            </Row>
            <Row>
              <Form.Item label="Notional currency: ">
                <Select
                  showSearch
                  style={{ width: 100 }}
                  placeholder="Currency"
                  optionFilterProp="currencies"
                  onChange={onChange}
                  onFocus={onFocus}
                  onBlur={onBlur}
                  onSearch={onSearch}
                  filterOption={(input, option) =>
                    option.props.currencies
                      .toLowerCase()
                      .indexOf(input.toLowerCase()) >= 0
                  }
                >
                  <Option value="usd">USD</Option>
                  <Option value="kyd">KYD</Option>
                  <Option value="gbp">GBP</Option>
                </Select>
              </Form.Item>
              <Form.Item
                label="Underlying currency: "
                labelCol={{ span: 10 }}
                wrapperCol={{ span: 5 }}
              >
                <Select
                  showSearch
                  style={{ width: 180 }}
                  placeholder="Currency"
                  optionFilterProp="currencies"
                  onChange={onChange}
                  onFocus={onFocus}
                  onBlur={onBlur}
                  onSearch={onSearch}
                  filterOption={(input, option) =>
                    option.props.currencies
                      .toLowerCase()
                      .indexOf(input.toLowerCase()) >= 0
                  }
                >
                  <Option value="usd">USD</Option>
                  <Option value="kyd">KYD</Option>
                  <Option value="gbp">GBP</Option>
                </Select>
              </Form.Item>
            </Row>
            <Form.Item
              label="Strike Price: "
              labelCol={{ span: 8 }}
              wrapperCol={{ span: 16 }}
            >
              <InputNumber />
              <span className="ant-form-text">
                {" "}
                $currency
                {
                  // This is where the value of the currency form above should go
                }{" "}
              </span>
            </Form.Item>
            <Form.Item
              label="Market value: "
              labelCol={{ span: 8 }}
              wrapperCol={{ span: 16 }}
            >
              <span className="ant-form-text">
                -{" "}
                {
                  // VALUE
                }{" "}
                {
                  // currency from above
                }{" "}
                $currency
              </span>
            </Form.Item>
            <Form.Item
              label="Notional value: "
              labelCol={{ span: 8 }}
              wrapperCol={{ span: 16 }}
            >
              <span className="ant-form-text">
                -{" "}
                {
                  // VALUE
                }{" "}
                {
                  // currency from above
                }
                $currency
              </span>
            </Form.Item>
            <Divider />
            <Form.Item
              name="date-picker"
              label="Expiry date:"
              labelCol={{ span: 8 }}
              wrapperCol={{ span: 16 }}
            >
              <DatePicker />
            </Form.Item>
            <Form.Item
              name="time-picker"
              label="Expiry time:"
              labelCol={{ span: 8 }}
              wrapperCol={{ span: 16 }}
            >
              <TimePicker />
            </Form.Item>
          </Form>
          <Form>
            <Form.Item wrapperCol={{ span: 8, offset: 8 }}>
              <Button type="primary" htmlType="submit">
                Add
              </Button>
              <Button style={{ marginLeft: 8 }}>Cancel</Button>
            </Form.Item>
          </Form>
        </Col>
        <Col xs />
      </Row>
    </Grid>
  </>
);

export default Addtrade;
