import React from "react";
import { Typography, Button, Table, Tag } from "antd";
import ReportGenerate from "./reportgenerate";
import { Link } from "react-router-dom";
const { Title } = Typography;

class ReportPDF extends React.Component {
  state = {
    button: true,
    now: new Date(),
    date: this.props.date,
    trades: [
      {
        tradeid: "",
        buyer: "",
        seller: "",
        product: "",
        quantity: "",
        notionalcurrency: "",
        underlyingcurrency: "",
        strikeprice: "",
        marketvalue: "",
        notionalvalue: "",
        date: "",
        time: "",
        tags: ""
      },
      {
        tradeid: "",
        buyer: "",
        seller: "",
        product: "",
        quantity: "",
        notionalcurrency: "",
        underlyingcurrency: "",
        strikeprice: "",
        marketvalue: "",
        notionalvalue: "",
        date: "",
        time: "",
        tags: ""
      },
      {
        tradeid: "",
        buyer: "",
        seller: "",
        product: "",
        quantity: "",
        notionalcurrency: "",
        underlyingcurrency: "",
        strikeprice: "",
        marketvalue: "",
        notionalvalue: "",
        date: "2020-3-3",
        time: "",
        tags: []
      }
    ]
  };
  onDownload = e => {
    this.setState({
      button: false
    });
    console.log(`Report downloaded for ${this.state.date}`)
  };
  componentDidMount() {
    var now = this.state.now.getTime();
    console.log(`date format for now is ${now}`);
    var fixedDate = new Date(this.state.trades[2].date).getTime();
    console.log(`date format for trade is ${fixedDate}`);
    // active editable is meant to be for trades added today right?
    // I don't think we have data for these trades - maybe we have to filter it by
    // the trades available on the portfolio page ?
    var i;
    for (i = 0; i < this.state.trades.length; i++) {
      var expiry = new Date(this.state.trades[i].date).getTime();
      if (expiry <= now) {
        this.state.trades[i].tags = "Inactive";
      } else {
        this.state.trades[i].tags = "Active";
      }
    }
    this.state.trades
      .filter(trade => new Date(trade.date) > now.toString()) // this probably won't work because of formatting
      .forEach(trade => (trade.tags = "Active editable"));
    this.state.trades
      .filter(trade => trade.date == now)
      .forEach(trade => (trade.tags = "Active uneditable"));
    this.state.trades
      .filter(trade => trade.date < now)
      .forEach(trade => (trade.tags = "Inactive"));
  }

  render() {
    return (
      <Link
        to={{pathname: `/pdf/${this.state.date}`, state: {date: this.state.date}}}
      >
          <Button type="link">Download</Button>
      </Link>
    );
  }
}

export default ReportPDF;
