import React from "react";
import { Modal, Badge, Icon, Divider, Slider } from "antd";
import Alertlist from "./alertlist";
import axios from "axios";

class Notifications extends React.Component {
  state = { visible: false, sensitivity: ""};

  componentDidMount(){
    axios.get("http://127.0.0.1:5000/trades/verificationSensitivity/get")
      .then(response => {
        console.log(response)
        this.setState({sensitivity : response.data.result})
      })
      .catch(error => {
        console.log(error)
      })
  }

  showModal = e => {
    e.stopPropagation();
    this.setState({
      visible: true
    });
  };

  handleOk = e => {
    console.log(e);
    e.stopPropagation();
    this.setState({
      visible: false
    });
  };

  handleCancel = e => {
    console.log(e);
    e.stopPropagation();
    this.setState({
      visible: false
    });
    console.log(`sensitivity of ML was changed to ${this.state.sensitivity}`)
  };

  newSensitivity = e => {
    var sens = e;
    if (e === 100) {
      sens=5
    } else if (e === 75){
      sens=4
    }else if (e === 50){
      sens=3
    }else if (e === 25){
      sens=2
    }else if (e === 0){
      sens=1
    }
    this.setState({
      slidervalue: e,
    })
    axios.get(`http://127.0.0.1:5000/trades/verificationSensitivity/set/${sens}`)
      .then(response => {
        console.log(response)
      })
      .catch(error => {
        console.log(error)
      })
  };

  render() {
    return (
      <div onClick={this.showModal}>
        <span style={{ marginRight: 20 }}> My account </span>
        <Badge dot>
          <Icon size={32} type="bell" />
        </Badge>
        <Modal
          title="Notifications"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
          footer={null}
        >
        <Alertlist sensitivity={this.state.sensitivity}/>
          <Divider />
          <h3>Detection sensitivity</h3>
          <p>1 = least sensitive, 5 = most sensitive.</p>
          <Slider
            step={null}
            defaultValue={(this.state.sensitivity - 1) * 25}
            marks={{
              0: "1",
              25: "2",
              50: "3",
              75: "4",
              100: "5"
            }}
            tooltipVisible={false}
            onChange={this.newSensitivity}
          />
        </Modal>
      </div>
    );
  }
}
export default Notifications;
