import React from "react";
import { Table } from "antd";
import axios from "axios";
// import { Row, Col } from "react-flexbox-grid";
// import PortfolioChart from "./components/portfoliochart";
import Viewtrade from "./components/viewtrade";

class Portfolio extends React.Component {
  state = {
    data: [],
    columns: [
      {
        title: "Trade ID",
        dataIndex: "TradeID",
        key: "TradeID",
        render: text => (
          <Viewtrade
            tradeid={text}
          />
        )
      },
      {
        title: "Buying party",
        dataIndex: "BuyingParty",
        key: "BuyingParty"
      },
      {
        title: "Selling party",
        dataIndex: "SellingParty",
        key: "SellingParty"
      },
      {
        title: "Product",
        dataIndex: "Product",
        key: "Product"
      },
      {
        title: "Quantity",
        dataIndex: "Quantity",
        key: "Quantity"
      },
      {
        title: "Notional currency",
        dataIndex: "NotionalCurrency",
        key: "NotionalCurrency"
      },
      {
        title: "Underlying currency",
        dataIndex: "UnderlyingCurrency",
        key: "UnderlyingCurrency"
      },
      {
        title: "Strike price",
        dataIndex: "StrikePrice",
        key: "StrikePrice"
      },
      {
        title: "Notional Amount",
        dataIndex: "NotionalAmount",
        key: "NotionalAmount"
      },
      {
        title: "Expiry date",
        dataIndex: "MaturityDate",
        key: "MaturityDate"
      },
      {
        title: "Trade date",
        dataIndex: "Tdate",
        key: "Tdate"
      },
      {
        title: "Underlying Price",
        dataIndex: "UnderlyingPrice",
        key: "UnderlyingPrice"
      }
    ]
  };

  componentDidMount(){
    axios.get(`http://127.0.0.1:5000/trades/getBulk/onDate/2020-01-01`)
      .then( response => {
        this.setState({data : response.data.result})
        console.log(response.data.result)
      })
      .catch(error => {
        console.log(error)
      })
  }
  showModal = e => {
    e.stopPropagation();
    this.setState({
      visible: true
    });
  };

  handleOk = e => {
    e.stopPropagation();
    this.setState({
      visible: false
    });
  };

  handleCancel = e => {
    e.stopPropagation();
    this.setState({
      visible: false
    });
  };

  handleEdit = e => {
    e.stopPropagation();
    console.log(`Requesting to edit trade ${this.state.tradeid}`);
    // Take to the edit trade form for this trade id
    this.setState({
      visible: false
    });
  };

  handleDelete = e => {
    e.stopPropagation();
    console.log(`Requesting to delete trade ${this.state.tradeid}`);
    this.setState({ visible: false });
  };

  render() {
    return (
      <>
        <section style={{ margin: 20 }}>
          <h1 style={{ textAlign: "left" }}>Portfolio</h1>
          <h2>Trades today:</h2>
          <Table dataSource={this.state.trades} columns={this.state.columns} />
        </section>
      </>
    );
  }
}

export default Portfolio;
