import React from "react";
import { List, Table, Icon, Button, DatePicker } from "antd";
import Moment from "moment"
import ReportPDF from "./components/reportpdf";

const { RangePicker } = DatePicker;

class Reports extends React.Component {
  state = {
    list: ["2010-04-01", "2020-01-02"],
    dataSource: [
      {
        key: "1",
        file: "2020-01-09.csv"
      },
      {
        key: "2",
        file: "2020-01-08.csv"
      }
    ],
    columns: [
      {
        title: "File",
        dataIndex: "file",
        key: "file"
      },
      {
        title: "",
        key: "actions",
        width: 100,
        render: (text, record) => (
          <span>
            <a href="/pdf">
              {" "}
              <Icon type="download" /> Download
            </a>
          </span>
        )
      }
    ],
    dateString: ""
  };
  getDayBefore = (date) =>{
    date.setDate(date.getDate() - 1);
    return Moment(date).format('YYYY-MM-DD')
  }
  componentDidMount(){
    var date = new Date();
    var d1 = Moment(date).format('YYYY-MM-DD')
    var d2 = this.getDayBefore(d1)
    var d3 = this.getDayBefore(d2)
    var d4 = this.getDayBefore(d3)
    var d5 = this.getDayBefore(d4)
    var d6 = this.getDayBefore(d5)
    var d7 = this.getDayBefore(d6)
    console.log(Moment(date).format('YYYY-MM-DD'))
  }
  onChange = (date, dateString) => {
    this.setState({
      dateString: dateString
    });
  };
  handleGenerateToday = e => {
    var now = new Date();
    console.log(
      `Generate pdf for ${now.toLocaleDateString(
        "zh-CN"
      )} ${now.toLocaleTimeString()}`
    );
  };
  handleGenerateRange = e => {
    console.log(`Generate pdf for range ${this.state.dateString}`);
  };
  render() {
    return (
      <>
        <section style={{ margin: 20 }}>
          <h2>Generate reports:</h2>
          <span>Generate report of all trades made today: </span>
          <Button
            htmlType="submit"
            style={{ marginLeft: 8 }}
            type="primary"
            onClick={this.handleGenerateToday}
          >
            Generate
          </Button>
          <br />
          <br />
          Generate report between specific dates:{" "}
          <RangePicker style={{ marginLeft: 8 }} onChange={this.onChange} />
          <Button
            style={{ marginLeft: 8 }}
            type="primary"
            onClick={this.handleGenerateRange}
          >
            Generate
          </Button>
          <h1 style={{ textAlign: "left" }}>Previous Reports</h1>
          <List
            itemLayout="vertical"
            dataSource={this.state.list}
            renderItem={item => <List.Item extra={<ReportPDF date={item}/>}>{item}</List.Item>}
          />
          <Table
            dataSource={this.state.dataSource}
            columns={this.state.columns}
            size="middle"
          />
        </section>
      </>
    );
  }
}

export default Reports;
