import React from "react";
import { Layout, Table } from "antd";
import { Row, Col } from "react-flexbox-grid";
import PortfolioChart from "./components/portfoliochart";

const dataSource = [
  {
    key: "1",
    tradeid: 18561026,
    buyer: "AHG150",
    seller: "889GJK",
    product: "Car",
    quantity: 599,
    date: "2020-02-01"
  },
  {
    key: "2",
    tradeid: 689118260,
    buyer: "AHG650",
    seller: "829GJK",
    product: "Oil",
    quantity: 112,
    date: "2020-02-02"
  }
];

const columns = [
  {
    title: "Trade ID",
    dataIndex: "tradeid",
    key: "tradeid"
  },
  {
    title: "Buyer",
    dataIndex: "buyer",
    key: "buyer"
  },
  {
    title: "Seller",
    dataIndex: "seller",
    key: "seller"
  },
  {
    title: "Product",
    dataIndex: "product",
    key: "product"
  },
  {
    title: "Quantity",
    dataIndex: "quantity",
    key: "quantity"
  },
  {
    title: "Date",
    dataIndex: "date",
    key: "date"
  }
];

const Portfolio = () => (
  <>
    <section style={{ margin: 20 }}>
      <h1 style={{ textAlign: "left" }}>Portfolio</h1>
      <Row left="xs">
        <Col xs>
          <h1>Best performing products:</h1>
          <PortfolioChart />
        </Col>
      </Row>
      <Table dataSource={dataSource} columns={columns} />
    </section>
  </>
);

export default Portfolio;
