import React from "react";
import ViewtradeComponent from "./viewtradecomponent";

class Viewtrade extends React.Component {
  state = {
    visible: false,
    tradeid: this.props.tradeid,
    tradeData: {
      buyingParty: "ABCD123",
      sellingParty: "QWER987",
      productStock: "Stock",
      productName: "Volvo 240",
      quantity: "16",
      notionalCurrency: "USD",
      underlyingCurrency: "USD",
      strikePrice: "1020000",
      marketValue: "16320000",
      notionalValue: "16320000",
      expiryDate: "2020/06/19",
      expiryTime: "00:00:00"
    }
  };
  showModal = e => {
    e.stopPropagation();
    this.setState({
      visible: true
    });
  };

  handleOk = e => {
    e.stopPropagation();
    this.setState({
      visible: false
    });
  };

  handleCancel = e => {
    e.stopPropagation();
    this.setState({
      visible: false
    });
  };

  handleEdit = e => {
    e.stopPropagation();
    console.log(`Requesting to edit trade ${this.state.tradeid}`);
    // Take to the edit trade form for this trade id
    this.setState({
      visible: false
    });
  };

  handleDelete = e => {
    e.stopPropagation();
    console.log(`Requesting to delete trade ${this.state.tradeid}`);
    this.setState({ visible: false });
  };

  /// GET DATA for this trade id and set state tradeData

  render() {
    return (
      <ViewtradeComponent
        showModal={this.showModal}
        handleOk={this.handleOk}
        handleCancel={this.handleCancel}
        handleEdit={this.handleEdit}
        data={this.state}
      />
    );
  }
}
export default Viewtrade;
