import React from "react";
import { Card, Button } from "antd";
import { WarningOutlined } from "@ant-design/icons";
import Viewtrade from "./viewtrade";
import axios from "axios";

class Alertlist extends React.Component {
  state = {
    errorTrades: []
  };

  componentDidMount(){
      axios.get("http://127.0.0.1:5000/trades/badTrades/list")
        .then(response => {
          this.setState({ errorTrades : response.data.result})
          console.log(response.data)
        })
        .catch(error => {
          console.log(error)
        })
  }

  handleIgnore = (id) => {
    this.setState(prevState => ({
    errorTrades: prevState.errorTrades.filter(el => el != id )
    }));
  }

  render() {
    return (
      <div>
        {this.state.errorTrades.map(trade => (
          <Card
            style={{
              marginTop: 8,
              background: "#fffbe6",
              outline: "#ffe58f solid 1px"
            }}
            key={trade}
            bordered={false}
            size="small"
            type="warning"
            showIcon
          >
            <WarningOutlined style={{ color: "#faad14" }} />
            {<span style={{ marginLeft: 8 }}>Error detected for trade </span>}
            <Viewtrade tradeid={trade} />
            <Button onClick={this.handleIgnore(trade)}>
            Ignore
            </Button>
          </Card>
        ))}
      </div>
    );
  }
}
export default Alertlist;
