import React from "react";
import {
  Divider,
  Form,
  Select,
  Input,
  InputNumber,
  DatePicker,
  TimePicker,
  Button,
  Radio
  // Alert // testing
} from "antd";
import { Row, Col } from "react-flexbox-grid";
import "./addtrade.css";

const { Option } = Select;

function onChange(value) {
  console.log(`selected ${value}`);
}

function onBlur() {
  console.log("blur");
}

function onFocus() {
  console.log("focus");
}

function onSearch(val) {
  console.log("search:", val);
}

// function handleSubmit(e) {
//   e.preventDefault();
//   console.log("form inputs recieved:", e);
// }

class Addtrade extends React.Component {
  state = {
    disabled: false
  };

  enableField = () => {
    this.setState({
      disabled: false
    });
  };

  disableField = () => {
    this.setState({
      disabled: true
    });
  };

  render() {
    const onFinish = values => {
      values.preventDefault();
      console.log("form inputs recieved:", values);
    };

    return (
      <>
        <section style={{ textAlign: "center" }}>
          <h1 style={{ textAlign: "center" }}>Add new trade</h1>
        </section>
        <Row>
          <Col xs />
          <Col>
            <Form layout="inline" style={{ marginTop: 16 }} onSubmit={onFinish}>
              <Row around="xs">
                <Col>
                  <Form.Item
                    label="Buying party:"
                    rules={[{ required: true, message: "Please input buyer!" }]}
                  >
                    <Input placeholder="Yourself" />
                  </Form.Item>
                </Col>
                <Col>
                  <Form.Item label="Selling party:">
                    <Input placeholder="Them" />
                  </Form.Item>
                </Col>
              </Row>
              <br />
              <Row around="xs">
                <Col />
                <Col>
                  <Form.Item label="Product / Stock:">
                    <Radio.Group buttonStyle="solid">
                      <Radio.Button value="a" onClick={this.enableField}>
                        Product
                      </Radio.Button>
                      <Radio.Button value="b" onClick={this.disableField}>
                        Stock
                      </Radio.Button>
                    </Radio.Group>
                  </Form.Item>
                </Col>
                <Col />
              </Row>
              <br />
              <Row between="xs">
                <Col>
                  <Form.Item label="Product name: ">
                    {
                      // Function to disable this option if the user selected "stock above"
                    }
                    <Select
                      disabled={this.state.disabled}
                      showSearch
                      style={{ width: 160 }}
                      placeholder="Product name"
                      // optionFilterProp="children"
                      onChange={onChange}
                      onFocus={onFocus}
                      onBlur={onBlur}
                      onSearch={onSearch}
                      filterOption={(input, option) =>
                        option.props.children
                          .toLowerCase()
                          .indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      <Option value="jack">Jack</Option>
                      <Option value="lucy">Lucy</Option>
                      <Option value="tom">Tom</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col>
                  <Form.Item label="Quantity: ">
                    <Input
                      // value={value.number}
                      onChange={this.handleNumberChange}
                      style={{ width: 170 }}
                    />
                  </Form.Item>
                </Col>
              </Row>
              <br />
              <Row between="xs">
                <Col>
                  <Form.Item label="Notional currency: ">
                    <Select
                      showSearch
                      style={{ width: 135 }}
                      placeholder="Currency"
                      optionFilterProp="currencies"
                      onChange={onChange}
                      onFocus={onFocus}
                      onBlur={onBlur}
                      onSearch={onSearch}
                      filterOption={(input, option) =>
                        option.props.currencies
                          .toLowerCase()
                          .indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      <Option value="usd">USD</Option>
                      <Option value="kyd">KYD</Option>
                      <Option value="gbp">GBP</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col>
                  <Form.Item label="Underlying currency: ">
                    <Select
                      showSearch
                      style={{ width: 120 }}
                      placeholder="Currency"
                      optionFilterProp="currencies"
                      onChange={onChange}
                      onFocus={onFocus}
                      onBlur={onBlur}
                      onSearch={onSearch}
                      filterOption={(input, option) =>
                        option.props.currencies
                          .toLowerCase()
                          .indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      <Option value="usd">USD</Option>
                      <Option value="kyd">KYD</Option>
                      <Option value="gbp">GBP</Option>
                    </Select>
                  </Form.Item>
                </Col>
              </Row>
              <br />
              <Row between="xs">
                <Form.Item label="Strike Price: ">
                  <InputNumber style={{ width: 140 }} />
                  <span className="ant-form-text">
                    {" "}
                    $currency
                    {
                      // This is where the value of the currency form above should go
                    }{" "}
                  </span>
                </Form.Item>
              </Row>
              <Row>
                <Form.Item label="Market value: ">
                  <span className="ant-form-text">
                    -{" "}
                    {
                      // VALUE
                    }{" "}
                    {
                      // currency from above
                    }{" "}
                    $currency
                  </span>
                </Form.Item>
              </Row>
              <Row>
                <Form.Item label="Notional value: ">
                  <span className="ant-form-text">
                    -{" "}
                    {
                      // VALUE
                    }{" "}
                    {
                      // currency from above
                    }
                    $currency
                  </span>
                </Form.Item>
              </Row>
              <Divider />
              <Row left="xs">
                <Form.Item name="date-picker" label="Expiry date:">
                  <DatePicker />
                </Form.Item>
                <Form.Item name="time-picker" label="Expiry time:">
                  <TimePicker />
                </Form.Item>
              </Row>
              <Divider />
              <Row around="xs">
                <Form.Item>
                  <Button type="primary" htmlType="submit">
                    Add
                  </Button>
                  <Button style={{ marginLeft: 8 }}>Cancel</Button>
                </Form.Item>
              </Row>
            </Form>
          </Col>
          <Col xs />
        </Row>
      </>
    );
  }
}
export default Addtrade;
