import React from "react";
import { Modal, Button, Popconfirm, Descriptions } from "antd";
import { ExclamationCircleOutlined } from "@ant-design/icons";
import Edittrade from "./edittrade";
import axios from "axios";

function ViewtradeComponent(props) {
  var data = [];
  axios.get(`/trades/get/${props.data.tradeid}`)
    .then( response => {
      data = response.data.result
    })
    .catch(error => {
      date = []
    })
  return (
    <>
      <Button type="link" onClick={props.showModal}>{props.data.tradeid}</Button>
        <Modal
          width={700}
          visible={props.data.visible}
          title="View trade"
          onOk={props.handleOk}
          onCancel={props.handleCancel}
          footer={[
            <Button key="cancel" onClick={props.handleCancel}>
              Back
            </Button>,
            <Edittrade default={props.data.tradeData}/>,
            <Popconfirm
              title="Are you sure you want to delete?"
              onConfirm={props.handleDelete}
              icon={<ExclamationCircleOutlined style={{ color: "red" }} />}
              okText="Yes"
              okType="danger"
              cancelText="No"
            >
              <Button key="delete" type="danger">
                Delete
              </Button>
            </Popconfirm>
          ]}
        >
          <Descriptions title={`Trade ${props.data.tradeid}`}>
            <Descriptions.Item label="Buying party">
              {data.BuyingParty}
            </Descriptions.Item>
            <Descriptions.Item label="Selling party">
              {props.data.tradeData.SellingParty}
            </Descriptions.Item>
            <Descriptions.Item label="Product Name">
              {props.data.tradeData.Product}
            </Descriptions.Item>
            <Descriptions.Item label="Quantity">
              {props.data.tradeData.Quantity}
            </Descriptions.Item>
            <Descriptions.Item label="Notional currency">
              {props.data.tradeData.NotionalCurrency}
            </Descriptions.Item>
            <Descriptions.Item label="Underlying currency">
              {props.data.tradeData.UnderlyingCurrency}
            </Descriptions.Item>
            <Descriptions.Item label="Strike price">
              {props.data.tradeData.StrikePrice}
            </Descriptions.Item>
            <Descriptions.Item label="Market value">
              {props.data.tradeData.NotionalAmount}
            </Descriptions.Item>
            <Descriptions.Item label="Notional value">
              {props.data.tradeData.UnderlyingPrice}
            </Descriptions.Item>
            <Descriptions.Item label="Expiry date">
              {props.data.tradeData.MaturityDate}
            </Descriptions.Item>
          </Descriptions>
        </Modal>
    </>
  );
}

export default ViewtradeComponent;
