import React from "react";
import axios from "axios";
import qs from 'qs'
import AddtradeComponent from "./addtradeComponent";

class Addtrade extends React.Component {
  state = {
    tradeId: "",
    createTradedata: {
      buyingParty: "",
      sellingParty: "",
      productName: "",
      quantity: "",
      notionalCurrency: "",
      underlyingCurrency: "",
      strikePrice: "",
      expiryDate: "",
      tDate: "",
    },
    disabled: false,
    visible: false,
    buyingParty: {
      value: "",
      valid: "",
      help: ""
    },
    sellingParty: {
      value: "",
      valid: "",
      help: ""
    },
    productStock: {
      value: "",
      valid: "",
      help: ""
    },
    productName: {
      value: ""
    },
    quantity: {
      value: "",
      valid: "",
      help: ""
    },
    notionalCurrency: {
      value: ""
    },
    underlyingCurrency: {
      value: ""
    },
    strikePrice: {
      value: "",
      valid: "",
      help: ""
    },
    expiryDate: {
      value: ""
    },
    expiryTime: {
      value: ""
    },
    currencyList: [],
    productList: [],
    companyList: [],
    isVerified: false,
    verifiedData: {
      marketValue: "",
      notionalValue: "",
      currentValue: ""
    },
    verifyData: {
      quantity: "",
      sellingParty: "",
      productName: "",
      notionalCurrency: "",
      strikePrice: ""
    }
  };

  componentDidMount(){                                        //This is an example of a lifecycle method. This method will only be called once, when the component is "mounted"/first rendered
    axios.get("http://127.0.0.1:5000/company/list")
      .then(response => {
        this.setState({companyList : response.data.result})
        console.log(this.state.companyList)
      })
      .catch(error => {
        console.log(error)
      })
    axios.get("http://127.0.0.1:5000/product/list")
      .then(response => {
        this.setState({productList : response.data.result})
        console.log(this.state.productList)
      })
      .catch(error => {
        console.log(error)
      })
    axios.get("http://127.0.0.1:5000/currency/list")
      .then(response => {
        this.setState({currencyList : response.data.result})
        console.log(response.data)
      })
      .catch(error => {
        console.log(error)
      })
  }

  showModal = e => {
    e.stopPropagation();
    this.setState({
      visible: true
    });
  };

  handleOk = e => {
    e.stopPropagation();
    console.log(e);
    this.setState({
      visible: false
    });
  };

  handleCancel = e => {
    e.stopPropagation();
    console.log(e);
    this.setState({
      visible: false
    });
  };

  enableField = () => {
    this.setState({
      disabled: false
    });
  };

  disableField = () => {
    this.setState({
      disabled: true
    });
  };
  handleNumber = event => {
    event.preventDefault();
    const { name, value } = event.target;
    var validator = "validating";
    var helpMsg = "";
    console.log(`handling ${name} set to ${value}`);
    if (name === "quantity" || name === "strikePrice") {
      if (!Number(value) & (value !== "") || Number(value) < 0) {
        validator = "error";
        helpMsg =
          name === "quantity"
            ? "Enter a valid quantity"
            : "Enter a valid strike price";
      }
    }
    this.setState(prevState => ({
      // must take into account previous state as is an object with changing properties
      ...prevState, //Spread notation means to copy all same properties of previous states object "errors"
      [name]: {
        value: value,
        valid: validator,
        help: helpMsg
      }
    }));
  };
  handleBuyer = value => {
    var validator = "";
    var helpMsg = "";
    console.log(
      `Checking if ${value} is equal to ${this.state.sellingParty.value}`
    );
    if (value == this.state.sellingParty.value) {
      validator = "error";
      helpMsg = "Enter a valid buyer";
    }
    this.setState(prevState => ({
      ...prevState,
      buyingParty: {
        value: value,
        valid: validator,
        help: helpMsg
      }
    }));
  };
  handleSeller = value => {
    var validator = "";
    var helpMsg = "";
    console.log(
      `Checking if ${value} is equal to ${this.state.buyingParty.value}`
    );
    if (value == this.state.buyingParty.value) {
      validator = "error";
      helpMsg = "Enter a valid seller";
    }
    this.setState(prevState => ({
      ...prevState,
      sellingParty: {
        value: value,
        valid: validator,
        help: helpMsg
      }
    }));
  };

  handleNotional = v => {
    console.log(console.log(`selected notional ${v}`));
    this.setState(prevState => ({
      ...prevState,
      notionalCurrency: {
        value: v
      }
    }));
  };

  handleDropdown = val => {
    this.setState(prevState => ({
      ...prevState,
      productName: {
        value: val
      }
    }));
  };

  handleUnderlying = v => {
    console.log(console.log(`selected underlying ${v}`));
    this.setState(prevState => ({
      ...prevState,
      underlyingCurrency: {
        value: v,
        valid: "",
        help: ""
      }
    }));
  };

  handleExpirydate = (d, ds) => {
    this.setState(prevState => ({
      ...prevState,
      expiryDate: {
        value: ds
      }
    }));
  };

  handleExpirytime = (d, ds) => {
    this.setState(prevState => ({
      ...prevState,
      expiryTime: {
        value: ds
      }
    }));
  };

  isStock = () => {
    if(this.state.productStock.value === "Stock"){
      this.setState(prevState => ({
          ...prevState,
          productName: {
            value: "Stock"
          }
      }))
    }
  }

  handleVerify = () => {
    this.isStock()
    this.setState(prevState => ({
        ...prevState,
        verifyData: {
          quantity: this.state.quantity.value,
          sellingParty: this.state.sellingParty.value,
          productName: this.state.productName.value,
          notionalCurrency: this.state.notionalCurrency.value,
          strikePrice: this.state.strikePrice.value
        }
    }))
  };

  getVerify = async () =>{
    await this.handleVerify();
    const options = {
      headers: {
        'Content-Type': 'application/json',
      }
    };
    axios
      .post("http://127.0.0.1:5000/trades/verify", this.state.verifyData)
      .then(response => {
        console.log(response.data.result)
        this.setState(prevState => ({
            ...prevState,
            verifiedData: {
              marketValue: response.data.result.marketValue,
              notionalValue: response.data.result.notionalValue,
              currentValue: response.data.result.currentPrice
            }
        }))
      })
      .catch(error => {
        console.log("ERROR: =============", error.response)
    })
    this.setState({isVerified : true})
  }

  handleSubmit = async () => {
    this.isStock()
    var d = new Date();
    var h = d.getHours();
    var m = d.getMinutes();
    var currtime = "2020-01-01 " + h + ":" + m
    await this.setState(prevState => ({
        ...prevState,
        createTradedata: {
          buyingParty: this.state.buyingParty.value,
          sellingParty: this.state.sellingParty.value,
          productName: this.state.productName.value,
          quantity: this.state.quantity.value,
          notionalCurrency: this.state.notionalCurrency.value,
          underlyingCurrency: this.state.underlyingCurrency.value,
          strikePrice: this.state.strikePrice.value,
          expiryDate: this.state.expiryDate.value,
          tDate: currtime
        }
    }))
    axios
      .post("http://127.0.0.1:5000/trades/create", this.state.createTradedata)
      .then(response => {
        console.log(response.data.result)
        this.setState({ [this.state.tradeId] : response.data.result.TradeID})
      })
      .catch(error => {
        console.log("ERROR: =============", error.response)
    })
  }

  render() {
    return (
      <AddtradeComponent
        showModal={this.showModal}
        handleOk={this.handleOk}
        handleCancel={this.handleCancel}
        handleNotional={this.handleNotional}
        handleNumber={this.handleNumber}
        handleBuyer={this.handleBuyer}
        handleSeller={this.handleSeller}
        handleUnderlying={this.handleUnderlying}
        handleDropdown={this.handleDropdown}
        handleExpirydate={this.handleExpirydate}
        handleExpirytime={this.handleExpirytime}
        handleVerify={this.getVerify}
        enableField={this.enableField}
        disableField={this.disableField}
        data={this.state}
        handleSubmit={this.handleSubmit}
      />
    );
  }
}
export default Addtrade;
