import React from "react";
import {
  Divider,
  Form,
  Select,
  Input,
  DatePicker,
  TimePicker,
  Button,
  Radio,
  AutoComplete
} from "antd";
import { Row } from "react-flexbox-grid";
import "./addtrade.css";

const { Option } = Select;

const companylist = [
  {
    value: "Tesla"
  },
  {
    value: "Apple"
  },
  {
    value: "eBay"
  },
  {
    value: "Alibaba"
  }
];

class Addtrade extends React.Component {
  state = {
    disabled: false,
    buyingParty: "",
    sellingParty: "",
    gender: "",
    productName: "",
    quantity: 0,
    notionalCurrency: "",
    underlyingCurrency: "",
    strikePrice: "",
    expiryDate: "",
    expiryTime: "",
    errors: {
      quantity: 0,
      strikePrice: 0,
      expiryDate: ""
    },
    validateStatus: {
      buyingParty: "",
      sellingParty: "",
      quantity: ""
    }
  };

  enableField = () => {
    this.setState({
      disabled: false
    });
  };

  disableField = () => {
    this.setState({
      disabled: true
    });
  };

  validator = field => {
    let validateStatus = this.state.validateStatus.field;

    if (field === "buyingParty" || field === "sellingParty") {
      if (this.state.buyingParty === this.state.sellingParty) {
        validateStatus = "error";
        this.setState({ validateStatus });
      }
    }
  };

  handleChange = event => {
    // event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;
    let validateStatus = this.state.validateStatus;
    switch (name) {
      case "quantity":
        errors.quantity = !Number(value)
          ? // ? "Quanity must be a valid number"
            " "
          : "";
        break;
      case "strikePrice":
        errors.strikePrice = !Number(value)
          ? // ? "Strike price must be valid number"
            " "
          : "";
        break;
      default:
        break;
    }
    this.setState({ errors, [name]: value });
    if (errors.quantity.length > 0) {
      let quantstatus = { ...validateStatus.quantity };
      quantstatus = "error";
      validateStatus.quantity = quantstatus;
      this.setState({ validateStatus });
    } else {
      let quantstatus = { ...validateStatus.quantity };
      quantstatus = "";
      validateStatus.quantity = quantstatus;
      this.setState({ validateStatus });
    }
  };

  handleDropdown = value => {
    this.setState({ productName: value });
  };

  handleNotional = v => {
    this.setState({ notionalCurrency: v });
  };

  handleUnderlying = v => {
    this.setState({ underlyingCurrency: v });
  };

  handleExpirydate = (d, ds) => {
    this.setState({ expiryDate: ds });
  };

  handleExpirytime = (d, ds) => {
    this.setState({ expiryTime: ds });
  };

  render() {
    const { errors } = this.state;
    const onFinish = values => {
      values.preventDefault();
      console.log("form inputs recieved:", values);
    };

    return (
      <>
        <section style={{ textAlign: "center", marginTop: 16, marginLeft: 20 }}>
          <h1 style={{ textAlign: "center" }}>Add new trade</h1>
          <Row around="xs">
            <Form layout="inline" style={{ marginTop: 16 }} onSubmit={onFinish}>
              <Form.Item
                label="Buying party:"
                hasFeedback
                validateStatus={this.state.validateStatus.buyingParty}
                // rules={[{ required: true, message: "Please input buyer!" }]}
              >
                <Input
                  placeholder="Yourself"
                  value={this.state.buyingParty}
                  name="buyingParty"
                  onChange={this.handleChange}
                />
              </Form.Item>
              <Form.Item
                name="sellingParty"
                label="Selling party:"
                hasFeedback
                validateStatus={this.state.validateStatus.sellingParty}
              >
                <Input
                  placeholder="Them"
                  value={this.state.sellingParty}
                  name="sellingParty"
                  onChange={this.handleChange}
                />
              </Form.Item>
              <br />
              <Form.Item label="Product / Stock:" style={{ marginTop: 16 }}>
                <Radio.Group
                  buttonStyle="solid"
                  name="gender"
                  onChange={this.handleChange}
                >
                  <Radio.Button value="Product" onClick={this.enableField}>
                    Product
                  </Radio.Button>
                  <Radio.Button value="Stock" onClick={this.disableField}>
                    Stock
                  </Radio.Button>
                </Radio.Group>
              </Form.Item>
              <br />
              <Form.Item label="Product name: " style={{ marginTop: 16 }}>
                <Select
                  disabled={this.state.disabled}
                  showSearch
                  style={{ width: 170 }}
                  placeholder="Product name"
                  filterOption={(input, option) =>
                    option.props.children
                      .toLowerCase()
                      .indexOf(input.toLowerCase()) >= 0
                  }
                  onChange={this.handleDropdown}
                >
                  <Option value="jack">Jack</Option>
                  <Option value="lucy">Lucy</Option>
                  <Option value="tom">Tom</Option>
                </Select>
              </Form.Item>
              <Form.Item
                label="Quantity: "
                style={{ marginTop: 16 }}
                validateStatus={this.state.validateStatus.quantity}
                name="quantity"
                // help="Please input a valid number!"
              >
                <Input
                  name="quantity"
                  value={this.state.quantity}
                  onChange={this.handleChange}
                  style={{ width: 170 }}
                />
                {errors.quantity.length > 0 && ( //This creates an error message as user is typing to validate quantity
                  <label for="quantity"> {errors.quantity}</label>
                )}
              </Form.Item>
              <br />
              <Form.Item label="Notional currency: " style={{ marginTop: 16 }}>
                <Select
                  showSearch
                  style={{ width: 170 }}
                  placeholder="Currency"
                  optionFilterProp="currencies"
                  filterOption={(input, option) =>
                    option.props.currencies
                      .toLowerCase()
                      .indexOf(input.toLowerCase()) >= 0
                  }
                  onChange={this.handleNotional}
                >
                  <Option value="usd">USD</Option>
                  <Option value="kyd">KYD</Option>
                  <Option value="gbp">GBP</Option>
                </Select>
              </Form.Item>
              <Form.Item
                label="Underlying currency: "
                style={{ marginTop: 16 }}
                name="underlyingCurrency"
              >
                <Select
                  showSearch
                  style={{ width: 170 }}
                  placeholder="Currency"
                  optionFilterProp="currencies"
                  filterOption={(input, option) =>
                    option.props.currencies
                      .toLowerCase()
                      .indexOf(input.toLowerCase()) >= 0
                  }
                  onChange={this.handleUnderlying}
                >
                  <Option value="USD">USD</Option>
                  <Option value="KYD">KYD</Option>
                  <Option value="GBP">GBP</Option>
                </Select>
              </Form.Item>
              <br />
              <Row around="xs">
                <section style={{ textAlign: "right" }}>
                  <Form.Item label="Strike Price: " style={{ marginTop: 16 }} name="strikePrice">
                    <Input
                      style={{ width: 140 }}
                      name="strikePrice"
                      value={this.state.strikePrice}
                      onChange={this.handleChange}
                    />
                    <span className="ant-form-text">
                      {" "}
                      {this.state.notionalCurrency.toUpperCase()}
                    </span>
                    {errors.strikePrice.length > 0 && (
                      <label for="strikePrice"> {errors.strikePrice}</label>
                    )}
                  </Form.Item>
                  <br />
                  <Form.Item label="Market value: ">
                    <span className="ant-form-text">
                      {" "}
                      {this.state.strikePrice}{" "}
                      {this.state.notionalCurrency.toUpperCase()}
                    </span>
                  </Form.Item>
                  <br />
                  <Form.Item label="Notional value: ">
                    <span className="ant-form-text">
                      {" "}
                      {this.state.strikePrice}{" "}
                      {this.state.notionalCurrency.toUpperCase()}
                    </span>
                  </Form.Item>
                </section>
              </Row>
              <Divider />
              <Form.Item name="date-picker" label="Expiry date:">
                <DatePicker onChange={this.handleExpirydate} />
                {errors.expiryDate.length > 0 && (
                  <label for="date-picker" textAllign="left">
                    {errors.expiryDate}
                  </label>
                )}
              </Form.Item>
              <Form.Item name="time-picker" label="Expiry time:">
                <TimePicker onChange={this.handleExpirytime} />
              </Form.Item>
              <Divider />
              <Form.Item>
                <Button type="primary" htmlType="submit">
                  Add
                </Button>
                <Button style={{ marginLeft: 8 }}>Cancel</Button>
              </Form.Item>
            </Form>
          </Row>
        </section>
      </>
    );
  }
}
export default Addtrade;
