import React from "react";
import ViewtradeComponent from "./viewtradecomponent";
import axios from "axios";

class Viewtrade extends React.Component {
  state = {
    visible: false,
    tradeid: this.props.tradeid,
    data: {}
  };
  showModal = e => {
    e.stopPropagation();
    this.setState({
      visible: true
    });
  };

  handleOk = e => {
    e.stopPropagation();
    this.setState({
      visible: false
    });
  };

  handleCancel = e => {
    e.stopPropagation();
    this.setState({
      visible: false
    });
  };

  handleEdit = e => {
    e.stopPropagation();
    console.log(`Requesting to edit trade ${this.state.tradeid}`);
    // Take to the edit trade form for this trade id
    this.setState({
      visible: false
    });
  };

  handleDelete = e => {
    e.stopPropagation();
    axios.delete(`/trades/delete/${this.state.tradeid}`)
      .then( response => {
        console.log(response.data)
      })
      .catch(error => {
        console.log(error)
      })
    this.forceUpdate();
    this.setState({ visible: false });
  };

  /// GET DATA for this trade id and set state tradeData
  getTradedata = () => {
    axios.get(`http://127.0.0.1:5000/trades/get/${this.state.tradeid}`)
      .then( response => {
        this.setState({data : response.data.result})
      })
      .catch(error => {
        console.log(error)
      })
  }

  render() {
    this.getTradedata();
    return (
      <ViewtradeComponent
        showModal={this.showModal}
        handleOk={this.handleOk}
        handleCancel={this.handleCancel}
        handleEdit={this.handleEdit}
        data={this.state}
        handleDelete={this.handleDelete}
      />
    );
  }
}
export default Viewtrade;
