import React from "react";
import { Button, Radio, Select } from "antd";
import { Row, Col } from "react-flexbox-grid";
import Dashchart from "./components/dashchart";
import axios from "axios";
const { Option } = Select;

class Dashboard extends React.Component {
  state = {
    disabled: true,
    loading: false,
    dataType: "",
    productlist: ["Car", "Oil", "Gun", "Gold"],
    stocklist: ["Tesla", "Apple", "eBay", "Alibaba"],
    currencylist: ["KYD", "USD", "GBP"],
    options: "",
    tofetch: [],
    dofetch: [],
    data: []
  };
  onChange = e => {
    console.log(`user has selected to display a ${e.target.value}`);
    var options = this.state.options;
    if (e.target.value == "product") {
      options = this.state.productlist.map(product => (
        <Option key={product} value={product}>
          {product}
        </Option>
      ));
      this.setState({
        options: options,
        disabled: true
      });
    } else if (e.target.value == "stock") {
      options = this.state.stocklist.map(stock => (
        <Option key={stock} value={stock}>
          {stock}
        </Option>
      ));
      this.setState({
        options: options
      });
    } else {
      options = this.state.currencylist.map(currency => (
        <Option key={currency} value={currency}>
          {currency}
        </Option>
      ));
      this.setState({
        options: options
      });
    }
    this.setState({
      disabled: false,
      dataType: e.target.value,
      placeholder: `Please select ${e.target.value}`,
      tofetch: []
    });
  };

  onSelect = e => {
    console.log(`user chosen to fetch ${e}`);
    this.setState({
      tofetch: [e]
    });
  };

  onFetch = () => {
    var type = this.state.dataType;
    var values = this.state.tofetch;
    console.log(`user has requested to fetch ${values} from ${type}`);
    this.setState({ loading: true, dofetch: values });
    // fetch data by sending the backend ${type} and ${values}
    this.setState({ data: [], loading: false }); // set the data from backend
  };

  render() {
    return (
      <section style={{ textAlign: "center" }}>
        <Row around="xs">
          <Col xs={12} sm={3} lg={3}>
            <h2 style={{ marginLeft: 20, marginTop: 20 }}>Data</h2>
            <Col xs={12}>
              <Radio.Group
                buttonStyle="solid"
                name="productstockcurrency"
                size="medium"
                onChange={this.onChange}
              >
                <Radio.Button value="product">Product</Radio.Button>
                <Radio.Button value="stock">Stock</Radio.Button>
                <Radio.Button value="currency">Currency</Radio.Button>
              </Radio.Group>
            </Col>
            <Col xs={12}>
              <Select
                mode="multiple"
                disabled={this.state.disabled}
                style={{ marginTop: 8, width: "70%" }}
                placeholder={this.state.placeholder}
                onChange={this.onSelect}
              >
                {this.state.options}
              </Select>
            </Col>
            <Col xs={12}>
              <Button
                style={{ marginTop: 8 }}
                loading={this.state.loading}
                onClick={this.onFetch}
              >
                Fetch data
              </Button>
            </Col>
          </Col>
          <Col xs={12} sm={9} lg={9}>
            <h2 style={{ marginLeft: 30, marginTop: 20, textAlign: "left" }}>
              {this.state.dofetch.toString()}
            </h2>
            <Dashchart data={this.state.data} />
          </Col>
        </Row>
      </section>
    );
  }
}

export default Dashboard;
