import React from "react";
import { Modal, Button, Badge, Icon, Divider, Slider } from 'antd';
import Alertlist from "./alertlist";

function formatter(value) {
  return `${value}%`;
}

class Notifications extends React.Component {
  state = { visible: false };

  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  handleCancel = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  render() {
    return (
      <div onClick={this.showModal}>
        <span style={{ marginRight: 20 }}> My account </span>
        <Badge dot>
          <Icon size={32} type="bell" />
        </Badge>
        <Modal
          title="Notifications"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
          footer={null}
        >
          <Alertlist />
          <Divider />
          <h3>Detection sensitivity</h3>
          <br />
          0%
          <Slider style={{ marginLeft: 'auto', marginRight: 'auto', width: 350 }} tipFormatter={formatter} />
          100%
        </Modal>
      </div>
    );
  }
}
export default Notifications
