import React from 'react';
import { Form, Select, Input, InputNumber, DatePicker, Switch, Slider, Button, Radio } from 'antd';
import { Grid, Row, Col } from 'react-flexbox-grid';
import './App.css';

const { Option } = Select;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 5 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 12 },
  },
};

const App = () => (
  <>
    <section style={{textAlign: 'center'}}>
      <h1 style={{textAlign: 'center'}}>Add new trade</h1>
      // <img style={{width: '40px', height: '40px'}} src="https://gw.alipayobjects.com/zos/rmsportal/KDpgvguMpGfqaHPjicRK.svg"/>
    </section>
    <Grid fluid>
    <Row>
    <Col xs />
    <Col xs={6} >
    <Form layout="inline" style={{ marginTop: 32 }}>
      <Form.Item
        label="Buying party:"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 8 }}
      >
         <Input placeholder="Yourself" />
      </Form.Item>
      <Form.Item
        label="Selling party:"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 8 }}
      >
         <Input placeholder="Them" />
      </Form.Item>
      </Form>
      <Form>
      <Form.Item
        label="Product / Stock:"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 8 }}
      >
        <Radio.Group defaultValue="a" buttonStyle="solid">
          <Radio.Button value="a">Product</Radio.Button>
          <Radio.Button value="b">Stock</Radio.Button>
        </Radio.Group>
      </Form.Item>
      <Form.Item
        label="Product name:"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 8 }}
      >
        <Select defaultValue="Product name" style={{ width: 192 }}>
          <Option value="jack">jack</Option>
          <Option value="lucy">lucy</Option>
          <Option value="disabled" disabled>disabled</Option>
          <Option value="yiminghe">yiminghe</Option>
        </Select>
      </Form.Item>
      <Form.Item
        label="Quantity: "
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 8 }}
      >
        <InputNumber />
      </Form.Item>
      <Form.Item>

      </Form.Item>
      <Form.Item wrapperCol={{ span: 8, offset: 8 }}>
        <Button type="primary" htmlType="submit">
          Add
        </Button>
        <Button style={{ marginLeft: 8 }}>
          Cancel
        </Button>
      </Form.Item>
    </Form>
    </Col>
    <Col xs />
    </Row>
    </Grid>
  </>
);

export default App;
