import React from "react";
import axios from "axios";
import AddtradeComponent from "./addtradeComponent";

class Addtrade extends React.Component {
  state = {
    disabled: false,
    visible: false,
    buyingParty: {
      value: "",
      valid: "",
      help: ""
    },
    sellingParty: {
      value: "",
      valid: "",
      help: ""
    },
    productStock: {
      value: "",
      valid: "",
      help: ""
    },
    productName: {
      value: ""
    },
    quantity: {
      value: "",
      valid: "",
      help: ""
    },
    notionalCurrency: {
      value: ""
    },
    underlyingCurrency: {
      value: ""
    },
    strikePrice: {
      value: "",
      valid: "",
      help: ""
    },
    expiryDate: {
      value: ""
    },
    expiryTime: {
      value: ""
    },
    currencyList: [],
    productList: [],
    companyList: [],
    isVerified: false,
    verifiedData: [],
    verifyData: {
      quantity: "",
      sellingParty: "",
      productName: "",
      notionalCurrency: "",
      strikePrice: ""
    }
  };

  componentDidMount(){                                        //This is an example of a lifecycle method. This method will only be called once, when the component is "mounted"/first rendered
    axios.get("http://127.0.0.1:5000/company/list")
      .then(response => {
        this.setState({companyList : response.data.result})
        console.log(this.state.companyList)
      })
      .catch(error => {
        console.log(error)
      })
    axios.get("http://127.0.0.1:5000/product/list")
      .then(response => {
        this.setState({productList : response.data.result})
        console.log(this.state.productList)
      })
      .catch(error => {
        console.log(error)
      })
    axios.get("http://127.0.0.1:5000/currency/list")
      .then(response => {
        this.setState({currencyList : response.data.result})
        console.log(response.data)
      })
      .catch(error => {
        console.log(error)
      })
  }

  showModal = e => {
    e.stopPropagation();
    this.setState({
      visible: true
    });
  };

  handleOk = e => {
    e.stopPropagation();
    console.log(e);
    this.setState({
      visible: false
    });
  };

  handleCancel = e => {
    e.stopPropagation();
    console.log(e);
    this.setState({
      visible: false
    });
  };

  enableField = () => {
    this.setState({
      disabled: false
    });
  };

  disableField = () => {
    this.setState({
      disabled: true
    });
  };
  handleNumber = event => {
    event.preventDefault();
    const { name, value } = event.target;
    var validator = "validating";
    var helpMsg = "";
    console.log(`handling ${name} set to ${value}`);
    if (name === "quantity" || name === "strikePrice") {
      if (!Number(value) & (value !== "") || Number(value) < 0) {
        validator = "error";
        helpMsg =
          name === "quantity"
            ? "Enter a valid quantity"
            : "Enter a valid strike price";
      }
    }
    this.setState(prevState => ({
      // must take into account previous state as is an object with changing properties
      ...prevState, //Spread notation means to copy all same properties of previous states object "errors"
      [name]: {
        value: value,
        valid: validator,
        help: helpMsg
      }
    }));
  };
  handleBuyer = value => {
    var validator = "";
    var helpMsg = "";
    console.log(
      `Checking if ${value} is equal to ${this.state.sellingParty.value}`
    );
    if (value == this.state.sellingParty.value) {
      validator = "error";
      helpMsg = "Enter a valid buyer";
    }
    this.setState(prevState => ({
      ...prevState,
      buyingParty: {
        value: value,
        valid: validator,
        help: helpMsg
      }
    }));
  };
  handleSeller = value => {
    var validator = "";
    var helpMsg = "";
    console.log(
      `Checking if ${value} is equal to ${this.state.buyingParty.value}`
    );
    if (value == this.state.buyingParty.value) {
      validator = "error";
      helpMsg = "Enter a valid seller";
    }
    this.setState(prevState => ({
      ...prevState,
      sellingParty: {
        value: value,
        valid: validator,
        help: helpMsg
      }
    }));
  };

  handleNotional = v => {
    console.log(console.log(`selected notional ${v}`));
    this.setState(prevState => ({
      ...prevState,
      notionalCurrency: {
        value: v
      }
    }));
  };

  handleDropdown = val => {
    this.setState(prevState => ({
      ...prevState,
      productName: {
        value: val
      }
    }));
  };

  handleUnderlying = v => {
    console.log(console.log(`selected underlying ${v}`));
    this.setState(prevState => ({
      ...prevState,
      underlyingCurrency: {
        value: v,
        valid: "",
        help: ""
      }
    }));
  };

  handleExpirydate = (d, ds) => {
    this.setState(prevState => ({
      ...prevState,
      expiryDate: {
        value: ds
      }
    }));
  };

  handleExpirytime = (d, ds) => {
    this.setState(prevState => ({
      ...prevState,
      expiryTime: {
        value: ds
      }
    }));
  };

  handleVerify = e => {
    // market value = strike price * quantity in underlying currency
    // notional value = current currency price * quantity in underlying currency
    e.preventDefault();
    if(this.state.productStock.value === "Stock"){
      this.setState(prevState => ({
          ...prevState,
          productName: {
            value: "Stock"
          }
      }))
    }
    this.setState(prevState => ({
        ...prevState,
        verifyData: {
          quantity: this.state.quantity.value,
          sellingParty: this.state.sellingParty.value,
          productName: this.state.productName.value,
          notionalCurrency: this.state.notionalCurrency.value,
          strikePrice: this.state.strikePrice.value
        }
    }))
    // send these to API
  };

  getVerify = () =>{
    console.log(`PRODUCT NAME: ${this.state.productName.value}`);
    console.log(this.state.verifyData)
    console.log("hiiiii")
    axios
      .post("http://127.0.0.1:5000/trades/verify", this.state.verifyData)
      .then(response => {
        console.log(response)
        this.setState({verifiedData : response})
      })
      .catch(error => {
        console.log(error)
      })
    this.setState({isVerified : true})
  }
  
  render() {
    return (
      <AddtradeComponent
        showModal={this.showModal}
        handleOk={this.handleOk}
        handleCancel={this.handleCancel}
        handleNotional={this.handleNotional}
        handleNumber={this.handleNumber}
        handleBuyer={this.handleBuyer}
        handleSeller={this.handleSeller}
        handleUnderlying={this.handleUnderlying}
        handleDropdown={this.handleDropdown}
        handleExpirydate={this.handleExpirydate}
        handleExpirytime={this.handleExpirytime}
        handleVerify={this.handleVerify}
        enableField={this.enableField}
        disableField={this.disableField}
        data={this.state}
      />
    );
  }
}
export default Addtrade;
